mapper.select:
SELECT b.bno bno, b.title title, b.content content, b.writer writer, b.regdate regdate, b.updatedate updateDate, f.fileName fileName
FROM tbl_board b LEFT JOIN tbl_board_file f ON b.bno = f.bno
WHERE b.bno = #{bno}

mapper.update:
UPDATE tbl_board
SET title = #{title}, content = #{content}, writer = #{writer}, updateDate = NOW()
WHERE bno = #{bno}
